import './App.css';
import React, {useState, useEffect} from 'react'
import {Button, FormControl, Input, InputLabel} from "@material-ui/core"
import Message from "./Message"
import {db} from "../firebase"
import firebase from "firebase"

function App() {
const [input, setInput] = useState("");
const [messages, setMessages] = useState([
  {username: 'sonny',message: 'hey'},
  {username: 'qazi',message: 'hey hey'}
]);
const [username, setUsername] = useState("")
//console.log(input)
//console.log(messages)

useEffect(() => {
  db.collection('messages').onSnapshot(snapshot => {
    setMessages(snapshot.docs.map(doc => (
      {id: doc.id, 
       message: doc.data()}
    )))
  })
},[])

useEffect(() => {
  setUsername(prompt('Please enter your name'))
}, [])

const sendMessage = (event) => {
  event.preventDefault();
  // setMessages([...messages, input]) //Keep the current and append the ending one
  setMessages([...messages, {username: username, text: input}]) //Keep the current and append the ending one
  setInput("");
}

  return (
    <div className="App">
      <h1>Facebook Messenger Clone</h1>
      <h2>Welcome {username} </h2>
      <form>
        <FormControl>
          <InputLabel>Enter a message...</InputLabel>
          <Input value={input} onChange={event => setInput(event.target.value)}/>
          <Button disabled={!input} variant="contained" color="primary" type="submit" onClick={sendMessage}>Send Message</Button>
        </FormControl>
      </form>

      {/*messages themselves */}

      {
        messages.map(message => (
          <Message 
            username={username} 
            message={message}
          />
          //<p>{message}</p>
        ))
      }

    </div>
  );
}

export default App;
