import React, {useEffect, useState} from 'react';
import './Todo.css';
import {List, ListItemText, ListItem, ListItemAvatar, Button, Modal} from '@material-ui/core'
import db from "./firebase"
import { makeStyles } from '@material-ui/core/styles';

const useStyles = makeStyles((theme) => ({
    paper: {
        position: 'absolute',
        width: 400,
        backgroundColor: theme.palette.background.paper,
        border: '2px solid #000',
        boxShadow: theme.shadows[5],
        padding: theme.spacing(2,4,3)
    }, 
}))

function Todo(props) {
    const classes = useStyles(); 
    const [open, setOpen] = useState(false)
    const [input, setInput] = useState("")

    const updateTodo = () => {
        setOpen(false); 
    }

    return (
        <>
        <Modal
            open={open}
            onClose={e => setOpen(false)}
        >
            <div className={classes.paper}>
                <h1> I am a modal</h1>
                <input value={input} onChange={event => setInput(event.target.value)}/>
                <Button onClick={e => setOpen(false)}>Update Todo</Button>
                </div>
            </Modal> 
            <List className="todo_list">
                <ListItem>
                    <ListItemAvatar>
                    </ListItemAvatar>
                        <ListItemText primary={props.todo.todo} secondary='Tomorrow ⏰'/>
                </ListItem>
                <button onClick={e => setOpen(true) }>Edit</button>
                <Button onClick={event => db.collection('todos').doc(props.todo.id).delete()}>
                    ❌ Delete Me!!!
                    </Button>
            </List>
        </>
    )
}

export default Todo
