import React, {useEffect, useState} from 'react';
import './Todo.css';
import {List, ListItemText, ListItem, ListItemAvatar, Button, Modal} from '@material-ui/core'
import db from "./firebase"

function Todo(props) {
    const [open, setOpen] = useState(false)

    const handleOpen = () => {
        setOpen(true); 
    };

    const handleClose = () => {
        setOpen(false); 
    };

    return (
        <>
        <Modal
            open={handleOpen}
            onClose={e => setOpen(false)}
        >
            <div>
                <h1> I am a modal</h1>
                <button onClick={setOpen(false)}/>
                </div>
            </Modal> 
        <List className="todo_list">
            <ListItem>
                <ListItemAvatar>
                </ListItemAvatar>
                    <ListItemText primary={props.todo.todo} secondary='Tomorrow ⏰'/>
            </ListItem>
            <button onClick={e => setOpen(true) }>Edit</button>
            <Button onClick={event => db.collection('todos').doc(props.todo.id).delete()}>
                ❌ Delete Me!!!
                </Button>
        </List>
        </>
    )
}

export default Todo
