import './App.css';
import React, {useEffect, useState} from 'react';

function App() {

  const [todos, setTodos] = useState(['Go take a shit','Take dog for a walk', 'Take rubbish out']);
  const [input, setInput] = useState('');

  return (
    <div className="App">
      <h1>Hello world</h1>
      <input value={input}/>
      <button>Add Todo</button>

      <ul>
        {todos.map(todo => (
          <li>{todo}</li>
          ))}
      </ul>
    </div>
  );
}

export default App;
