import React, {useEffect, useState} from 'react';
import './Todo.css';
import {List, ListItemText, ListItem, ListItemAvatar, Button} from '@material-ui/core'
import db from "./firebase"

function Todo(props) {

    return (
        <List className="todo_list">
            <ListItem>
                <ListItemAvatar>
                </ListItemAvatar>
                    <ListItemText primary={props.todo.todo} secondary='Tomorrow ⏰'/>
            </ListItem>
            <Button onClick={event => db.collection('todos').doc(props.todo.id).delete()}>
                ❌ Delete Me!!!
                </Button>
        </List>
    )
}

export default Todo
